                                                function saatkarsilastir($ilk_saat, $son_saat)
                                                {
                                                    $ilk = strtotime($ilk_saat);
                                                    $son = strtotime($son_saat);
                                                    if ($ilk < $son) {
                                                        return 1;
                                                    } else {
                                                        return 0;
                                                    }
                                                }

                                                $saat1 = date('H:i:s');
                                                $saat2 = "12:15:00";
                                                $am = saatkarsilastir($saat1, $saat2);
                                                if ($am == 1) {
                                                    $xdaylater = "+1d";
                                                } else {
                                                    $xdaylater = "+2d";
                                                }

