                                                        $xdaylater = "+1d";
                                                    } else {
                                                        $xdaylater = "+2d";
                                                    }

                                                    function tarihkarsilastir($ilk_tarih, $son_tarih, $yilbasi)
                                                    {
                                                        $ilk = strtotime($ilk_tarih);
                                                        $son = strtotime($son_tarih);
                                                        $yil = strtotime($yilbasi);
                                                        if ($ilk > $son && $ilk < $yil) {
                                                            return 1;
                                                        } else {
                                                            return 0;
                                                        }
                                                    }

