                                                function saatkarsilastir($ilk_saat, $son_saat)
                                                {
                                                    $ilk = strtotime($ilk_saat);
                                                    $son = strtotime($son_saat);
                                                    if ($ilk < $son) {
                                                        return 1;
                                                    } else {
                                                        return 0;
                                                    }
                                                }

                                                $saat1 = date('H:i:s');
