                    function RoundUp3($num){
                        if ($num > (int)$num + 0.3){
                            $roundup = (int)$num + 1;
                        }
                        elseif ($num < 0.3 && $num > 0){
                            $roundup = 1;
                        }
                        else{
                            $roundup = (int)$num;
                        }
                        return $roundup;
                    }
